import React, { useState } from 'react';
import '../App.css';

function CalculatorForm() {
  const [code, setCode] = useState('');
  const [t, setT] = useState(0);
  const [nc, setNc] = useState(0);
  const [nm, setNm] = useState(0);
  const [uc, setUc] = useState(0);
  const [Pc, setPc] = useState(0);
  const [Pm, setPm] = useState(0);
  const [PUE, setPUE] = useState(0);
  const [result, setResult] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('/api/calculate/carbon-emission/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ java_code: code, t, nc, nm, uc, Pc, Pm, PUE }),
    });
    const data = await response.json();
    setResult(data.carbon_emission);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <textarea
          value={code}
          onChange={(e) => setCode(e.target.value)}
          placeholder="Enter your Java code here"
        />
        <input
          type="number"
          value={t}
          onChange={(e) => setT(parseFloat(e.target.value))}
          placeholder="Running time (hours)"
        />
        <input
          type="number"
          value={nc}
          onChange={(e) => setNc(parseInt(e.target.value))}
          placeholder="Number of cores"
        />
        <input
          type="number"
          value={nm}
          onChange={(e) => setNm(parseInt(e.target.value))}
          placeholder="Size of memory available (GB)"
        />
        <input
          type="number"
          value={uc}
          onChange={(e) => setUc(parseFloat(e.target.value))}
          placeholder="Core usage factor"
        />
        <input
          type="number"
          value={Pc}
          onChange={(e) => setPc(parseFloat(e.target.value))}
          placeholder="Power draw of a computing core (Watt)"
        />
        <input
          type="number"
          value={Pm}
          onChange={(e) => setPm(parseFloat(e.target.value))}
          placeholder="Power draw of memory (Watt)"
        />
        <input
          type="number"
          value={PUE}
          onChange={(e) => setPUE(parseFloat(e.target.value))}
          placeholder="PUE (efficiency coefficient)"
        />
        <button type="submit">Calculate</button>
      </form>
      {result && <div>Carbon Emission: {result} kg CO2</div>}
    </div>
  );
}

export default CalculatorForm;
